pragma solidity ^0.8.8;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

// Contract address 0x4298A5aF5AFa8200B9fBb3A6b94b113413B6747d

import "../node_modules/@openzeppelin/contracts/access/Ownable.sol";

contract GuessNumber is Ownable{

    address winner;
    address _owner;

    uint8 public i_secretNumber;
    uint public fee;
    uint public prize;
    uint private gameAmount;
    bool guessed = true;
    bool a = false;

    error GuessNumber_GameIsFinished(address);
    error GuessNumber_GameInProcess(address);
    error GuessNumber_WinnerCannotPlay(address);
    error GuessNumber_NotEnoughMoney();
    error GuessNumber_NotWinner(address);

    //Set the number to guess, the owner of the game, and the games that have been played so far.
    constructor(){
        fee = 10**14 wei;
        _owner = msg.sender;
        winner = msg.sender;
        gameAmount = 0;
    }

    function tryToGuess(uint8 myNumber) public payable {
        if(guessed){
            revert GuessNumber_GameIsFinished(msg.sender);
        }
        //require(msg.sender != _owner, "Owner cannot play the game");
        if(msg.value < fee){
            revert GuessNumber_NotEnoughMoney();
        }
        if(msg.sender == winner){
            revert GuessNumber_WinnerCannotPlay(msg.sender);
        }
        (bool success_pay, ) = address(this).call{value: msg.value}("");
        prize = address(this).balance;
        if (myNumber == i_secretNumber) {
            guessed = true;
            winner = msg.sender;
            (bool success_receive, ) = winner.call{value: address(this).balance}("");
        }
    }

    function createGame(uint8 secretNumber) public payable{
        if(!guessed){
            revert GuessNumber_GameInProcess(msg.sender);
        }
        if(msg.sender != winner){
            revert GuessNumber_NotWinner(msg.sender);
        }
        guessed = false;
        gameAmount++;
        i_secretNumber = secretNumber;      
    }

    function checkGuessed() public view returns (bool) {
        return guessed;
    }

    function gameNumber() public view returns (uint){
        return gameAmount;
    }

    function checkFee() public view returns (uint){
        return fee;
    }

    function checkPrize() public view returns (uint){
        return prize;
    }

    function checkWinner() public view returns (address){
        return winner;
    }

    function checkSecretNumber() public view returns (uint8){
        return i_secretNumber;
    }
}
